/**
 * GRADIENT GENERATORS
 *
 * Flat colors look cheap. Real objects have gradients suggesting 3D form.
 * The key: 3-stop gradients with highlight → mid → shadow progression.
 *
 * These functions generate canvas gradient objects or CSS gradient strings.
 */
/**
 * Deep water background gradient (vertical)
 * Creates the characteristic dark blue that gets darker with depth
 */
export declare function deepWaterBackground(ctx: CanvasRenderingContext2D, height: number): CanvasGradient;
/**
 * Material gradient for 3D form (simulates light from above)
 * Use for: ROV body, equipment, solid objects
 */
export declare function material3D(ctx: CanvasRenderingContext2D, x: number, y: number, width: number, height: number, colors: {
    highlight: string;
    mid: string;
    shadow: string;
}): CanvasGradient;
/**
 * Radial glow gradient for light sources
 * Use for: Bioluminescence, ROV lights, eyes
 */
export declare function radialGlow(ctx: CanvasRenderingContext2D, x: number, y: number, innerRadius: number, outerRadius: number, colors: {
    core: string;
    mid: string;
    outer: string;
}): CanvasGradient;
/**
 * Player light gradient (cursor/flashlight in darkness)
 * Multiple layers for realistic falloff
 */
export declare function playerLight(ctx: CanvasRenderingContext2D, x: number, y: number, radius: number, intensity?: number): CanvasGradient[];
/**
 * ROV light beam (cone of light into darkness)
 */
export declare function lightBeam(ctx: CanvasRenderingContext2D, startY: number, endY: number): CanvasGradient;
/**
 * Vignette gradient (darkens edges, focuses center)
 */
export declare function vignette(ctx: CanvasRenderingContext2D, centerX: number, centerY: number, innerRadius: number, outerRadius: number, intensity?: number): CanvasGradient;
/**
 * Creature eye gradient (ominous glow)
 */
export declare function eyeGlow(ctx: CanvasRenderingContext2D, x: number, y: number, size: number, illumination?: number): CanvasGradient;
/**
 * CSS deep water background
 */
export declare function cssDeepWater(): string;
/**
 * CSS radial glow for bioluminescence
 */
export declare function cssBioGlow(color?: 'cyan' | 'warm'): string;
/**
 * CSS creature body (translucent, organic)
 */
export declare function cssCreatureBody(opacity?: number): string;
/**
 * CSS jellyfish bell (translucent, lit from above)
 */
export declare function cssJellyfishBell(): string;
/**
 * CSS distant creature (barely visible shape)
 */
export declare function cssDistantCreature(): string;
