/**
 * PARTICLE SYSTEMS
 *
 * Marine snow, bioluminescent particles, debris - these create atmosphere.
 * The key: varied sizes, speeds, and alphas. Uniform particles look artificial.
 */
export interface Particle {
    x: number;
    y: number;
    size: number;
    speed: number;
    alpha: number;
    phase?: number;
}
/**
 * Create marine snow particles (organic debris floating in water)
 * These drift slowly downward relative to camera
 */
export declare function createMarineSnow(count?: number): Particle[];
/**
 * Update marine snow (moves upward when ROV descends, or down normally)
 */
export declare function updateMarineSnow(particles: Particle[], direction?: 'up' | 'down'): void;
/**
 * Draw marine snow particles
 */
export declare function drawMarineSnow(ctx: CanvasRenderingContext2D, particles: Particle[], width: number, height: number): void;
/**
 * Sparse particles for extremely deep scenes (anglerfish, leviathan)
 */
export declare function createDeepParticles(count?: number): Particle[];
/**
 * Update deep particles with slight wobble
 */
export declare function updateDeepParticles(particles: Particle[], time: number): void;
/**
 * Generate CSS for marine snow overlay
 * Creates positioned divs that animate with CSS
 */
export declare function cssMarineSnowParticle(index: number): {
    style: string;
    animation: string;
};
/**
 * Generate HTML for a marine snow container
 */
export declare function htmlMarineSnow(count?: number): string;
/**
 * CSS for marine snow container and particles
 */
export declare const cssMarineSnowStyles = "\n  .marine-snow {\n    position: absolute;\n    inset: 0;\n    pointer-events: none;\n    overflow: hidden;\n  }\n  \n  .snow-particle {\n    position: absolute;\n    width: 2px;\n    height: 2px;\n    background: rgba(200, 220, 255, 0.4);\n    border-radius: 50%;\n    animation: snow-drift 8s linear infinite;\n  }\n  \n  .snow-particle:nth-child(odd) {\n    width: 3px;\n    height: 3px;\n    background: rgba(180, 200, 230, 0.3);\n  }\n  \n  @keyframes snow-drift {\n    0% { transform: translateY(-20px) translateX(0); opacity: 0; }\n    10% { opacity: 1; }\n    90% { opacity: 1; }\n    100% { transform: translateY(300px) translateX(20px); opacity: 0; }\n  }\n";
export interface BioParticle extends Particle {
    hue: number;
    pulseSpeed: number;
}
/**
 * Create bioluminescent particles (glowing organisms)
 */
export declare function createBioParticles(count?: number): BioParticle[];
/**
 * Draw bioluminescent particles with glow
 */
export declare function drawBioParticles(ctx: CanvasRenderingContext2D, particles: BioParticle[], width: number, height: number, time: number): void;
