/**
 * GARDEN SHARED EFFECTS
 *
 * Central export point for all shared visual effects and utilities used by garden scenes.
 * This follows the Scene-First architecture where scenes are self-contained and reuse
 * shared effects from this module.
 *
 * Usage in scenes:
 * ```typescript
 * import {
 *   drawSoftGardenWindow,
 *   softGarden,
 *   drawPollenParticles,
 * } from '../effects';
 *
 * export class GardenScene extends BaseCanvasScene {
 *   render(ctx, deltaTime) {
 *     ctx.fillStyle = softGarden.background;
 *     ctx.fillRect(0, 0, width, height);
 *     drawPollenParticles(ctx, this.particles, deltaTime);
 *     // ...
 *   }
 * }
 * ```
 */
export { softGarden, terrarium, gardenWindow, overgrown, withAlpha, interpolateColor, } from './colors.js';
export * from './gradients.js';
export * from './particles.js';
export * from './plants.js';
export * from './textures.js';
export { drawSoftGardenWindow, drawTerrariumView, drawGardenThroughGlass, drawOvergrownCamera, type GardenSceneOptions, } from './recipes.js';
export { vignette, radialGlow, material3D, } from '../../core/gradients.js';
export { createEyes, updateEyes, drawEyes, createEye, updateEye, drawEye, type Eye, } from '../../core/eyes.js';
export { calculateIllumination, drawPlayerLight, } from '../../core/lighting.js';
